import React, { Component } from 'react';
import Typed from 'react-typed';
import { Redirect } from "react-router-dom"

class Character extends Component {
    constructor(){
        super()
        this.state = {
            redirectTo: null,
            characerName: "",
            strength: 10,
            defense: 10,
            evasion: 10

        }
        this.handleChange = this.handleChange.bind(this)
        this.strengthIncDec = this.strengthIncDec.bind(this)
    }


    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    strengthIncDec(event) {
        if(this.text === "+") {
            console.log("yep")
        }
    }

    render() {
            return(
                <div className="row">
                <div className="col-3"></div>            
                    <div className="col-6">
                        <form>
                        <h2 className="main">Create A Character</h2>
                            <div className="form-row">                      
                                <div className="form-group col-12">
                                    <label htmlFor="characerName">Name:</label>
                                    <input
                                        type="text" 
                                        className="form-control"
                                        name="characterName" 
                                        id="characterName" 
                                        value={this.state.characterName} 
                                        onChange={this.handleChange} />
                                </div>
                                <div className="form-group col-12">
                                    <label htmlFor="password"></label>
                                    <input 
                                        type="text" 
                                        className="form-control"
                                        name="password" 
                                        id="password"  
                                        value={this.state.password} 
                                        onChange={this.handleChange} />
                                </div>

                                <div className="form-group col-1">
                                    <label htmlFor="strength"></label>
                                    <button type="button" className="btn btn-secondary" id="strength" onClick={this.statIncrease}>+</button>
                                    <div>
                                        {this.state.strength}
                                    </div>
                                    <button type="button" className="btn btn-secondary">-</button>

                                </div>
                                <div className="form-group col-1">
                                    <label htmlFor="defense"></label>
                                    <button type="button" className="btn btn-secondary" id="defense" onClick={this.statIncrease}>+</button>
                                    <div>
                                        {this.state.defense}
                                    </div>
                                    <button type="button" className="btn btn-secondary">-</button>

                                </div>
                                <div className="form-group col-1">
                                    <label htmlFor="evasion"></label>
                                    <button type="button" className="btn btn-secondary" id="evasion" onClick={this.statIncrease}>+</button>
                                    <div>
                                        {this.state.evasion}
                                    </div>
                                    <button type="button" className="btn btn-secondary">-</button>

                                </div>
                            </div>
                            {/* <button 

                                type="submit"
                                onClick={this.handleSubmit}>Submit</button> */}
                        </form>
                    </div>
                    <div className="col-3"></div>  
                </div>
            )
        }
    }


export default Character





